template:
html, body{
    width: 100%;
    height: 100%;
    padding: 0;
    margin: 0;
}

(function(){

    'use strict';

    const el = document.querySelector('node-red-camera');
    console.log('el:', el);

    el.addEventListener('message', function(e){
        console.log('message event:', e);
    }, false);

    el.addEventListener('error', function(err){
        console.log('err:', err);
    }); 

    el.addEventListener('streamavailable', function(data){
        console.log('streamavailable:', data);
    }); 

    el.addEventListener('imageavailable', function(data){
        console.log('imageavailable:', data);
    }); 

    el.addEventListener('videoavailable', function(data){
        console.log('videoavailable:', data);
    }); 

}());
